mapper.insert:
INSERT INTO sys_dict_item(
	CREATE_TIME,CREATOR_ID,DICT_ID,PID,ID,VALUE,STATUS,TEXT,UPDATE_TIME
)
VALUES(
	  now() ,  #{ creatorId } ,  #{ dictId } , #{ pid } , #{ id } ,  #{ value } ,  #{ status } ,  #{ text } ,  now()
)

mapper.select:
SELECT

FROM  sys_dict_item t
where id = #{ id }

select

from sys_dict_item t


		and instr(t.TEXT,#{ t.text })>0

	and t.DICT_ID=#{ t.dictId }



		order by t.${sort} ${sortOrder}


		order by t.CREATE_TIME DESC
