mapper.insert:
INSERT INTO SYS_DICT(
	CODE,CREATE_TIME,CREATOR_ID,ID,NAME,STATUS,UPDATE_TIME
)
VALUES(
	  #{ code } ,  now() ,  #{ creatorId } ,  #{ id } ,  #{ name } ,  #{ status } ,  now()
)

mapper.select:
SELECT

FROM  SYS_DICT d
where id = #{ id }

